mapper.insert:
INSERT	INTO BOARD 	(
						BOARD_ID
						, TITLE
						, CONTENT
						, CRT_DT
						, VIEW_COUNT
						, VIDEO_PATH
						, POSTER_PATH
						, BOARD_DIVISION
						, URL_ADDRESS
						, RECOMMEND_COUNT
						, EMAIL
					)
VALUES				(
						'BD-'|| TO_CHAR(SYSDATE, 'YYYYMMDDHH24') || '-' || LPAD(BOARD_ID_SEQ.NEXTVAL, 6, '0')
						, #{title}
						, #{content}
						, SYSDATE
						, 0
						, #{videoPath}
						, #{posterPath}
						, #{boardDivision}
						, #{urlAddress}
						, 0
						, #{email}
					)

mapper.select:
SELECT * FROM ( SELECT A.* , ROWNUM RNUM FROM ( SELECT B.BOARD_ID , B.TITLE , B.CONTENT , B.CRT_DT , B.VIEW_COUNT , B.VIDEO_PATH , B.POSTER_PATH , B.BOARD_DIVISION , B.URL_ADDRESS , B.RECOMMEND_COUNT , B.EMAIL , M.NAME , M.NICKNAME , M.PASSWORD , M.SALT , M.PROFILE_IMG , M.LOGIN_FAIL_COUNT , M.MEMBER_AUTHORITY , M.LOGIN_FAIL_TIME FROM BOARD B , MEMBER M WHERE B.EMAIL = M.EMAIL AND B.BOARD_DIVISION = #{boardDivision}
AND B.TITLE LIKE '%' || #{searchKeyword} || '%'
ORDER BY B.CRT_DT DESC ) A WHERE ROWNUM <= #{endRow} ) WHERE RNUM >= #{startRow}

mapper.update:
UPDATE	BOARD
SET		TITLE = #{title}
		, CONTENT = #{content}
		, VIDEO_PATH = #{videoPath}
		, POSTER_PATH = #{posterPath}
		, URL_ADDRESS = #{urlAddress}
WHERE	BOARD_ID = #{boardId}

UPDATE	BOARD
SET		VIEW_COUNT = VIEW_COUNT + 1
WHERE	BOARD_ID = #{boardId}
