mapper.insert:
INSERT	INTO BOARD 	(
						BOARD_ID
						, TITLE
						, CONTENT
						, CRT_DT
						, VIEW_COUNT
						, VIDEO_PATH
						, POSTER_PATH
						, BOARD_DIVISION
						, URL_ADDRESS
						, RECOMMEND_COUNT
						, EMAIL
					)
VALUES				(
						'BD-'|| TO_CHAR(SYSDATE, 'YYYYMMDDHH24') || '-' || LPAD(BOARD_ID_SEQ.NEXTVAL, 6, '0')
						, #{title}
						, #{content}
						, SYSDATE
						, 0
						, #{videoPath}
						, #{posterPath}
						, 0
						, #{urlAddress}
						, 0
						, #{email}
					)
